$_MODULE['<{jprestaspeedpack}prestashop>only-active-in-shop-context_e84ea6229e60ee4bc647d5abc5d7ea79'] = 'Bitte wählen Sie einen Shop zum Konfigurieren aus (Sie können keine Shopgruppe konfigurieren).';
